import React, { createContext, useContext, useReducer } from 'react';

// Initial state
const initialState = {
  user: null,
  orders: [],
  cart: []
};

// Action types
const ActionTypes = {
  SET_USER: 'SET_USER',
  SET_ORDERS: 'SET_ORDERS',
  ADD_TO_CART: 'ADD_TO_CART',
  REMOVE_FROM_CART: 'REMOVE_FROM_CART',
  CLEAR_CART: 'CLEAR_CART'
};

// Reducer function
const appReducer = (state, action) => {
  switch (action.type) {
    case ActionTypes.SET_USER:
      return { ...state, user: action.payload };
    case ActionTypes.SET_ORDERS:
      return { ...state, orders: action.payload };
    case ActionTypes.ADD_TO_CART:
      return { ...state, cart: [...state.cart, action.payload] };
    case ActionTypes.REMOVE_FROM_CART:
      return { ...state, cart: state.cart.filter(item => item.id !== action.payload) };
    case ActionTypes.CLEAR_CART:
      return { ...state, cart: [] };
    default:
      return state;
  }
};

// Create context
const AppContext = createContext();

// Context provider component
export const AppProvider = ({ children }) => {
  const [state, dispatch] = useReducer(appReducer, initialState);

  return (
    <AppContext.Provider value={{ state, dispatch }}>
      {children}
    </AppContext.Provider>
  );
};

// Custom hook to use the app context
export const useAppContext = () => {
  const context = useContext(AppContext);
  if (!context) {
    throw new Error('useAppContext must be used within an AppProvider');
  }
  return context;
};

// Action creators
export const setUser = (user) => ({ type: ActionTypes.SET_USER, payload: user });
export const setOrders = (orders) => ({ type: ActionTypes.SET_ORDERS, payload: orders });
export const addToCart = (item) => ({ type: ActionTypes.ADD_TO_CART, payload: item });
export const removeFromCart = (itemId) => ({ type: ActionTypes.REMOVE_FROM_CART, payload: itemId });
export const clearCart = () => ({ type: ActionTypes.CLEAR_CART });
