import React from 'react';

const pizzas = [
  { id: 1, name: 'Margherita', price: 10 },
  { id: 2, name: 'Pepperoni', price: 12 },
  { id: 3, name: 'Vegetarian', price: 11 },
];

function PizzaMenu() {
  return (
    <div className="container mx-auto mt-8">
      <h2 className="text-2xl font-bold mb-4">Our Menu</h2>
      <ul className="space-y-4">
        {pizzas.map((pizza) => (
          <li key={pizza.id} className="bg-white p-4 shadow rounded">
            <span className="font-bold">{pizza.name}</span>
            <span className="ml-4">${pizza.price}</span>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default PizzaMenu;
